PROCEDURE Proc_fixed_asset_GetDefaultCode (IN $prefix varchar(255), IN $suffix varchar(255), IN $base_value int, IN $length int)
BEGIN

  DECLARE $code varchar(255);
  DECLARE $code_exists int;

  SET $code_exists = 1;

  WHILE $code_exists > 0 DO
    SET $base_value = $base_value + 1;
    SET $code = CONCAT($prefix, LPAD($base_value, $length, '0'), $suffix);
    SELECT
      COUNT(*) INTO $code_exists
    FROM fixed_asset fa
    WHERE fa.fixed_asset_code = $code;
  END WHILE;

  SELECT
    $code;

END
$$

