--
-- Create function `getCurrentDepartmentOfFixedAsset`
--
CREATE DEFINER = 'root'@'localhost'
FUNCTION getCurrentDepartmentOfFixedAsset ($fixed_asset_id char(36), $department_id char(36))
RETURNS char(36) CHARSET utf8
BEGIN
  DECLARE current_department_id char(36);
  SET current_department_id = CASE WHEN
        EXISTS (SELECT
            dt.department_after_id
          FROM document d,
               document_details dt
          WHERE dt.fixed_asset_id = $fixed_asset_id
          AND d.document_id = dt.document_id) THEN (SELECT
              dt.department_after_id
            FROM document d,
                 document_details dt
            WHERE dt.fixed_asset_id = $fixed_asset_id
            AND d.document_id = dt.document_id
            AND d.transfer_date = (SELECT
                MAX(d2.transfer_date)
              FROM document d2,
                   document_details dt2
              WHERE dt2.fixed_asset_id = $fixed_asset_id
              AND dt2.document_id = d2.document_id)) ELSE $department_id END;

  RETURN current_department_id;
END
$$

