    FROM department de, document d, document_details dt
    INNER JOIN fixed_asset fa ON fa.fixed_asset_id = dt.fixed_asset_id
    WHERE de.department_id = dt.department_after_id
    AND d.document_id = dt.document_id
    AND d.transfer_date = (SELECT MAX(transfer_date) 
                                   FROM document d2, document_details dt2 
                                   WHERE dt2.fixed_asset_id = fa.fixed_asset_id
