  IF($where_condition <> '', (SELECT
      REPLACE($where_condition, 'WHERE', 'AND fa.')), ' '),
  '
    )
    UNION 
    (SELECT DISTINCT
          fa.fixed_asset_id,
          fa.fixed_asset_code,
          fa.fixed_asset_name,
          fa.department_id,
